const CardShop = () => {
  const [searchInput, setSearchInput] = useState("");
  const [filteredShops, setFilteredShops] = useState(shops);
  const [selectedShop, setSelectedShop] = useState(null);

  useEffect(() => {
    const filtered = shops.filter((shop) =>
      shop.name.toLowerCase().includes(searchInput.toLowerCase())
    );
    setFilteredShops(filtered);
  }, [searchInput]);

  const handleSearch = (e) => {
    setSearchInput(e.target.value);
  };

  const openModal = (shop) => {
    setSelectedShop(shop);
  };

  const closeModal = () => {
    setSelectedShop(null);
  };

  const generateWhatsAppLink = (shopName) => {
    const phoneNumber = "6288297894942"; // Ganti dengan nomor WhatsApp Anda
    const message = `Halo min, saya ingin membeli produk *${shopName}*. Pembayaran melalui *isi*.`;
    return `https://wa.me/${phoneNumber}?text=${encodeURIComponent(message)}`;
  };

  return (
    <>
      {/* SEARCH ----------------------------------------------------------------------------------- */}
      <div className="flex flex-col p-4 mt-24">
        <div className="flex justify-center gap-2">
          <input
            type="search"
            id="search-input"
            className="w-[65%] md:w-1/2 px-5 py-2 border rounded-lg bg-white shadow-lg shadow-secondary placeholder-gray-400 text-gray-700 focus:outline-none focus:shadow-outline"
            placeholder="Cari Product..."
            minLength="3"
            maxLength="50"
            value={searchInput}
            onChange={handleSearch}
          />
          <button
            id="search"
            onClick={handleSearch}
            className="btnSecondary shadow-lg shadow-white"
          >
            Search
          </button>
        </div>
      </div>

      {/* CARD SHOP --------------------------------------------------------------------------------- */}
      <div className="container mx-auto p-4 text-white">
        <div className="grid grid-cols-2 xs:grid-cols-2 sm:grid-cols-3 md:grid-cols-4 lg:grid-cols-5 gap-4">
          {filteredShops.map((shop) => (
            <div
              key={shop.id}
              className="bg-white rounded-lg overflow-hidden relative"
              style={{
                backdropFilter: "blur(20px)",
                backgroundColor: "rgba(115, 113, 113, 0.25)",
                border: "1px solid rgba(104, 103, 103, 0.18)",
              }}
            >
              <figure className="mb-1 bg-transparent overflow-hidden flex items-center justify-center w-full hover:scale-105 transition-all duration-100 ease-in-out">
                <img
                  src={shop.image}
                  alt={shop.name}
                  className="w-full h-36 sm:h-48 object-cover"
                />
              </figure>
              <div className="p-2 flex flex-col justify-between">
                <h2 className="font-normal mb-1 line-clamp-1">{shop.name}</h2>
                <h2 className="font-normal mb-1 text-secondary animate-pulse line-clamp-1">{shop.harga}</h2>
                <button
                  onClick={() => openModal(shop)}
                  className="inline-block mt-2 px-4 py-2 bg-secondary text-white rounded-lg hover:bg-primary transition"
                >
                  Beli
                </button>
              </div>
            </div>
          ))}
        </div>
      </div>

      {/* MODAL ---------------------------------------------------------------------------------*/}
      {selectedShop && (
        <div className="fixed inset-0 bg-gray-800 bg-opacity-75 flex items-center justify-center">
          <div className="bg-white p-6 rounded-lg shadow-lg max-w-md w-full">
            <div className="flex justify-between items-center mb-4">
              <h2 className="text-xl font-bold">{selectedShop.name}</h2>
              <button
                onClick={closeModal}
                className="w-6 text-2xl text-gray-500 hover:text-gray-700"
              >
                âœ–
              </button>
            </div>
            <img
              src={selectedShop.image}
              alt={selectedShop.name}
              className="w-64 h-60 mb-4"
            />
            <p className="text-primary font-semibold -mb-1">Deskripsi :</p>
            <p className="text-gray-700 mb-2">{selectedShop.description}</p>
            <p className="text-primary font-semibold">{selectedShop.harga}</p>
            <br />
            <a
              href={generateWhatsAppLink(selectedShop.name)}
              target="_blank"
              rel="noopener noreferrer"
              className="inline-block mt-2 px-4 py-2 bg-secondary text-white rounded-lg hover:bg-primary transition"
            >
              Beli
            </a>
          </div>
        </div>
      )}
    </>
  );
};

export default CardShop;
